const addExpense = (expense) => {
  return {
    type: 'ADD_EXPENSE',
    payload: expense
  }
};

const removeExpense = (id) => {
  return {
    type: 'REMOVE_EXPENSE',
    payload: id
  }
};

const getExpenses = () => {
  return {
    type: 'GET_EXPENSES'
  }
}

const getExpense = (id) => {
  return {
    type: 'GET_EXPENSE',
    payload: id
  }
};
const editExpense = (id, model) => {
  return {
    type: 'EDIT_EXPENSE',
    id,
    model
  }
}

export default {
  addExpense,
  removeExpense,
  getExpense,
  getExpenses,
  editExpense
}
