const defaultFilter = {
  text: '',
  sortBy: '',
  startDate: undefined,
  endDate: undefined
}
const initialState = {
  expenses: [
    {
      id: 0,
      description: 'January Rent',
      note: 'This was the final payment of the rent',
      amount: 1000,
      createdAt: 0
    }
  ],
  filters: {
    text: 'rent',
    sortBy: 'amount', //Date or amount
    startDate: undefined,
    endDate: undefined
  }
};
export default (state = defaultFilter, action) => {
  switch (action.type) {
    case 'SORT_BY_AMOUNT':
      return {
        ...state,
        sortBy: 'amount'
      }
    case 'SORT_BY_DATE':
      return {
        ...state,
        sortBy: 'date'
      }
    case 'SET_START_DATE':
      return {
        ...state,
        startDate: action.startDate
      }
    case 'SET_END_DATE':
      return {
        ...state,
        endDate: action.endDate
      }
    default:
      return state;
  }
}
