import React from 'react';
import { addExpense, removeExpense } from '../actions/expenses.js';
import { connect } from 'react-redux';

class ExpenseDashboardPage extends React.Component {
  constructor(props) {
    super(props);
  }
  componentWillMount() {
    this.props.dispatch(addExpense({ id: 1, name: 'Expense 1', description: 'Description 1', createdAt: 0}));
    this.props.dispatch(addExpense({ id: 2, name: 'Expense 2', description: 'Description 2', createdAt: 0}));
    this.props.dispatch(addExpense({ id: 3, name: 'Expense 3', description: 'Description 3', createdAt: 0}));
    this.props.dispatch(addExpense({ id: 4, name: 'Expense 4', description: 'Description 4', createdAt: 0}));
  }
  componentWillReceiveProps(nextProps) {

  }
  render() {
    return (
      <div>
        {this.props.expenses.map((item, key) => {
          console.log(this.props);
          return (
            <div key={key}>
              <h1>{item.name}</h1>
              <p>{item.description}</p>
              <button onClick={this.props.dispatch(removeExpense(item.id))}>Remove</button>
            </div>
          )
        })}
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    expenses: state.expenses,
    filters: state.filters
  }
}
export default connect(mapStateToProps)(ExpenseDashboardPage);
