// import React from 'react';
// import ReactDOM from 'react-dom';
import './index.css';
//import App from './App';
import * as serviceWorker from './serviceWorker';
import { createStore, combineReducers } from 'redux';


/*DEFAULT STATES*/
const defaultExpenses = [];
const defaultFilter = {
  text: '',
  sortBy: '',
  startDate: undefined,
  endDate: undefined
}
const initialState = {
  expenses: [
    {
      id: 0,
      description: 'January Rent',
      note: 'This was the final payment of the rent',
      amount: 1000,
      createdAt: 0
    }
  ],
  filters: {
    text: 'rent',
    sortBy: 'amount', //Date or amount
    startDate: undefined,
    endDate: undefined
  }
};

/*EXPENSE ACTIONS*/
const addExpense = (expense) => {
  return {
    type: 'ADD_EXPENSE',
    payload: expense
  }
};

const removeExpense = (id) => {
  return {
    type: 'REMOVE_EXPENSE',
    payload: id
  }
};

const getExpenses = () => {
  return {
    type: 'GET_EXPENSES'
  }
}

const getExpense = (id) => {
  return {
    type: 'GET_EXPENSE',
    payload: id
  }
};
const editExpense = (id, model) => {
  return {
    type: 'EDIT_EXPENSE',
    id,
    model
  }
}

/*FILTER ACTIONS*/
const sortByAmount = () => {
  return {
    type: 'SORT_BY_AMOUNT'
  }
}

const sortByDate = () => {
  return {
    type: 'SORT_BY_DATE'
  }
}
const sortByText = () => {
  return {
    type: 'SORT_BY_TEXT'
  }
}
const setStartDate = (startDate) => {
  return {
    type: 'SET_START_DATE',
    startDate
  }
}
const setEndDate = (endDate) => {
  return {
    type: 'SET_END_DATE',
    endDate
  }
}

const getFilteredExpenses = (expenses, filter) => {
  return expenses.filter((item) => {
    let startDateMatch = item.createdAt >= filter.startDate;
    let endDateMatch = item.createdAt <= filter.endDate;
    let textMatch = item.description.toLowerCase().includes(filter.text.toLowerCase());

    return startDateMatch && endDateMatch && textMatch;

  });
}
/*EXPENSE REDUCERS*/
const expensesReducer = (state = defaultExpenses, action) => {
    switch (action.type) {
      case 'ADD_EXPENSE':
        return [
          ...state,
          action.payload
        ];
      case 'REMOVE_EXPENSE':
        return state.filter(item => item.id !== action.payload);
      case 'EDIT_EXPENSE':
        return state.map((item) => {
          if (item.id === action.id) {
            return { ...item, ...action.model }
          }
          else {
            return item;
          }
        })
      default:
        return state;
    }
}
const filterReducer = (state = defaultFilter, action) => {
  switch (action.type) {
    case 'SORT_BY_AMOUNT':
      return {
        ...state,
        sortBy: 'amount'
      }
    case 'SORT_BY_DATE':
      return {
        ...state,
        sortBy: 'date'
      }
    case 'SET_START_DATE':
      return {
        ...state,
        startDate: action.startDate
      }
    case 'SET_END_DATE':
      return {
        ...state,
        endDate: action.endDate
      }
    default:
      return state;
  }
}

/*COMBINE ALL THE REDUCERS*/
const rootReducer = combineReducers({
  expensesReducer,
  filterReducer
});


/*STORE CREATE*/
const store = createStore(rootReducer);
store.subscribe(() => {
  console.log(store.getState());
})
store.dispatch(addExpense({ id: 0, description: 'İLK REDUX', note: 'THIS IS MY FIRST REDUX', amount: 500, createdAt: 0}));
store.dispatch(addExpense({ id: 1, description: 'İLK REDUXXX', note: 'THIS IS MY FIRST REDUXXXXXX', amount: 500, createdAt: 0}));
store.dispatch(addExpense({ id: 2, description: 'İLK REDUXXX', note: 'THIS IS MY FIRST REDUXXXXXX', amount: 500, createdAt: 0}));
store.dispatch(editExpense(2, { description: 'expense which is changed', note: 'THIS IS MY FIRST REDUXXXXXX', amount: 500, createdAt: 0}));
store.dispatch(removeExpense(1));
store.dispatch(sortByDate());
store.dispatch(setStartDate('01-01-1980'));
store.dispatch(setEndDate('01-03-1997'));


//ReactDOM.render(<App />, document.getElementById('root'));
// If you want your app to work offline and load faster, you can change
// unregister() to register() below. Note this comes with some pitfalls.
// Learn more about service workers: http://bit.ly/CRA-PWA
serviceWorker.unregister();
