// import React from 'react';
// import ReactDOM from 'react-dom';
import './index.css';
//import App from './App';
import * as serviceWorker from './serviceWorker';
import { createStore, combineReducers } from 'redux';


/*DEFAULT STATES*/
const defaultExpenses = [];
const defaultFilter = {
  text: '',
  sortBy: '',
  startDate: undefined,
  endDate: undefined
}
const initialState = {
  expenses: [
    {
      id: 0,
      description: 'January Rent',
      note: 'This was the final payment of the rent',
      amount: 1000,
      createdAt: 0
    }
  ],
  filters: {
    text: 'rent',
    sortBy: 'amount', //Date or amount
    startDate: undefined,
    endDate: undefined
  }
};


/*FILTER ACTIONS*/



/*EXPENSE REDUCERS*/
const expensesReducer = (state = defaultExpenses, action) => {
    switch (action.type) {
      case 'ADD_EXPENSE':
        return [
          ...state,
          action.payload
        ];
      case 'REMOVE_EXPENSE':
        return state.filter(item => item.id !== action.payload);
      case 'EDIT_EXPENSE':
        return state.map((item) => {
          if (item.id === action.id) {
            return { ...item, ...action.model }
          }
          else {
            return item;
          }
        })
      default:
        return state;
    }
};

const filterReducer = (state = defaultFilter, action) => {
  switch (action.type) {
    case 'SORT_BY_AMOUNT':
      return {
        ...state,
        sortBy: 'amount'
      }
    case 'SORT_BY_DATE':
      return {
        ...state,
        sortBy: 'date'
      }
    case 'SET_START_DATE':
      return {
        ...state,
        startDate: action.startDate
      }
    case 'SET_END_DATE':
      return {
        ...state,
        endDate: action.endDate
      }
    default:
      return state;
  }
}

/*COMBINE ALL THE REDUCERS*/
const rootReducer = combineReducers({
  expensesReducer,
  filterReducer
});

/*STORE CREATE*/
const store = createStore(rootReducer);
store.subscribe(() => {
  const state = store.getState();
  const filteredExpenses = getFilteredExpenses(state.expensesReducer, state.filterReducer);

  console.log(filteredExpenses);
});

store.dispatch(addExpense({ id: 0, description: 'İLK REDUX', note: 'THIS IS MY FIRST REDUX', amount: 500, createdAt: 1500}));
store.dispatch(addExpense({ id: 1, description: 'İLK REDUXXX', note: 'THIS IS MY FIRST REDUXXXXXX', amount: 500, createdAt: 2500}));
store.dispatch(addExpense({ id: 2, description: 'İLK REDUXXX', note: 'THIS IS MY FIRST REDUXXXXXX', amount: 500, createdAt: 0}));
store.dispatch(editExpense(2, { description: 'expense which is changed', note: 'THIS IS MY FIRST REDUXXXXXX', amount: 1500, createdAt: 0}));
store.dispatch(setStartDate(1000));
store.dispatch(setEndDate(3000));


