import React from 'react';
import { SingleDatePicker } from 'react-dates';
import moment from 'moment';
import 'react-dates/lib/css/_datepicker.css';

class ExpenseForm extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      form: {
        description: '',
        note: '',
        amount: 0,
        createdAt: moment(),
      },
      calendarFocused: false
    }
  }
  componentWillMount() {
    console.log(moment());
  }
  onFocusChange = ({ focused }) => {
    this.setState({
      calendarFocused: focused
    })
  }
  updateData = (key, value) => {
    let { form } = this.state;
    form[key] = value;

    this.setState({
      form
    })
  }

  handleSubmit = () => {

  }
  render() {
    let { form, calendarFocused } = this.state;

    return (
    <div>
      <form onSubmit={() => this.handleSubmit()}>
        <input type="text" placeholder="Description" autoFocus value={form.description} onChange={(e) => this.updateData('description', e.target.value )}/>
        <input type="number" placeholder="Amount" onChange={(e) => this.updateData('amount', e.target.value)}/>
        <SingleDatePicker
           date={this.state.createdAt}
           onDateChange={(createdAt) => this.updateData('createdAt',createdAt)}
           focused={calendarFocused}
           onFocusChange={({focused}) => this.onFocusChange({focused})}
        />
        <textarea placeholder="Add a note for your expense" onChange={(e) => this.updateData('note', e.target.value)}></textarea>
        <button type="submit">Submit</button>
      </form>
    </div>
  )
  }
}
export default ExpenseForm;
