// import React from 'react';
// import ReactDOM from 'react-dom';
import './index.css';
//import App from './App';
import * as serviceWorker from './serviceWorker';
import { createStore, combineReducers } from 'redux';


/*DEFAULT STATES*/
const defaultExpenses = [];
const defaultFilter = {
  text: '',
  sortBy: '',
  startDate: undefined,
  endDate: undefined
}
const initialState = {
  expenses: [
    {
      id: 0,
      description: 'January Rent',
      note: 'This was the final payment of the rent',
      amount: 1000,
      createdAt: 0
    }
  ],
  filters: {
    text: 'rent',
    sortBy: 'amount', //Date or amount
    startDate: undefined,
    endDate: undefined
  }
};

/*ACTIONS*/
const addExpense = (expense) => {
  return {
    type: 'ADD_EXPENSE',
    payload: expense
  }
};

const removeExpense = (expense) => {
  return {
    type: 'REMOVE_EXPENSE',
    payload: expense
  }
};

const getExpenses = () => {
  return {
    type: 'GET_EXPENSES'
  }
}

const getExpense = (id) => {
  return {
    type: 'GET_EXPENSE',
    payload: id
  }
};

/*REDUCERS*/
const expensesReducer = (state = defaultExpenses, action) => {
    switch (action.type) {
      case 'ADD_EXPENSE':
        return [
          ...state,
          action.payload
        ];
      case 'REMOVE_EXPENSE':
        return state.filter((item) => item.id !== action.payload);
      default:
        return state;
    }
}
const filterReducer = (state = defaultFilter, action) => {
  switch (action.type) {
    default:
      return state;
  }
}

/*COMBINE ALL THE REDUCERS*/
const rootReducer = combineReducers({
  expensesReducer,
  filterReducer
});


/*STORE CREATE*/
const store = createStore(rootReducer);

store.dispatch(addExpense({ id: 0, description: 'İLK REDUX', note: 'THIS IS MY FIRST REDUX', amount: 500, createdAt: 0}));
store.dispatch(addExpense({ id: 1, description: 'İLK REDUXXX', note: 'THIS IS MY FIRST REDUXXXXXX', amount: 500, createdAt: 0}));
store.dispatch(addExpense({ id: 2, description: 'İLK REDUXXX', note: 'THIS IS MY FIRST REDUXXXXXX', amount: 500, createdAt: 0}));
store.dispatch(removeExpense({ id: 1, description: 'İLK REDUXXX', note: 'THIS IS MY FIRST REDUXXXXXX', amount: 500, createdAt: 0}));
console.log(store.getState());

