import React from 'react';
import { connect, Provider } from 'react-redux';
import { addExpense } from '../actions/expenses.js';
import configureStore from '../store/store.js';
const store = configureStore();

class MainPage extends React.Component {
  constructor(props) {
    super(props);
  }
  componentWillMount() {
    this.props.dispatch(addExpense({ id: 1, name: 'Expense 1', description: 'Description 1', createdAt: 0}));
    this.props.dispatch(addExpense({ id: 2, name: 'Expense 2', description: 'Description 2', createdAt: 0}));
    this.props.dispatch(addExpense({ id: 3, name: 'Expense 3', description: 'Description 3', createdAt: 0}));
    this.props.dispatch(addExpense({ id: 4, name: 'Expense 4', description: 'Description 4', createdAt: 0}));
  }
  componentWillReceiveProps(nextProps) {
    console.log(nextProps);
  }
  render() {
    return (
      <Provider store={store}>
      <div>
        {this.props.expenses.map((item) => {
          return (
            <div>
              <h1>{item.name}</h1>
              <p>{item.description}</p>
            </div>
          )
        })}
      </div>
      </Provider>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    expenses: state.expenses,
    filters: state.filters
  }
}
export default connect(mapStateToProps)(MainPage);
